package com.letv.leauto.ecolink.ui.leradio_interface.data;/** * Created by shimeng on 14/12/23. */public class AudioAlbums {    private String name;    private String albumId;    private String singer;    private String images;    private String type;    private String sourceName;    private String sourceId;    public void setAlbumId(String albumId) {        this.albumId = albumId;    }    public String getAlbumId() {        return albumId;    }    public void setAuthor(String author) {        this.singer = author;    }    public String getAuthor() {        return this.singer;    }    public String getImg() {        return images;    }    public void setImg(String img) {        this.images = img;    }    public String getType() {        return type;    }    public void setType(String type) {        this.type = type;    }    public String getName() {        return name;    }    public void setName(String name) {        this.name = name;    }    public void setSourceName(String source) {        this.sourceName = source;    }    public String getSourceName() {        return this.sourceName;    }    public void setSourceId(String sourceId) {        this.sourceId = sourceId;    }    public String getSourceId() {        return this.sourceId;    }}