package com.letv.leauto.ecolink.ui.view;import android.content.Context;import android.graphics.Rect;import android.util.AttributeSet;import android.widget.TextView;/** * Created by shimeng on 14/6/2. * 为添加textview在失去焦点时也能进行跑马灯效果 */public class MarqueeText extends TextView {    public MarqueeText(Context con) {        super(con);    }    public MarqueeText(Context context, AttributeSet attrs) {        super(context, attrs);    }    public MarqueeText(Context context, AttributeSet attrs, int defStyle) {        super(context, attrs, defStyle);    }    @Override    public boolean isFocused() {        return true;    }    @Override    protected void onFocusChanged(boolean focused, int direction,                                  Rect previouslyFocusedRect) {    }}