package com.letv.leauto.ecolink.utils;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import java.io.IOException;import java.io.InputStream;import java.net.MalformedURLException;import java.net.URL;import java.net.URLConnection;/** * Created by shimeng on 14/6/30. */public class BitmapUtils {    static String url;    static Bitmap bitmap = null;    public final static Bitmap returnBitMap(String resourceUrl) {        url = resourceUrl;        new Thread(){            @Override            public void run() {                try {                    URL pictureUrl = new URL(url);                    URLConnection con = pictureUrl.openConnection();                    InputStream in = con.getInputStream();                    bitmap = BitmapFactory.decodeStream(in);                    in.close();                } catch (MalformedURLException e) {                    e.printStackTrace();                } catch (IOException e) {                    e.printStackTrace();                }            }        }.start();;        return bitmap;    }}